sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		//console.log('Note ID: ' + id + ' ' + idx);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	};

	$rootScope.$on('updateList', function(event, data) {	
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			console.log(c.data.noteID + ' ' + c.data.note);
			var str = JSON.stringify(c.data, null, 4);
			console.log(str);
		});
	});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var gr = new GlideRecord('x_snc_createnotes_note');
	gr.addQuery('user', gs.getUserID());
	gr.orderByDesc('sys_created_on');
	gr.query();

	while(gr.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj, gr, 'number, title, sys_id');
		noteObj.note = gr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}


	if(input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if(note.get(input.noteID)) {
			if(input.action == 'getNote') {
				data.note = {};
				$sp.getRecordValues(data.note, note, 'title, note, sys_id');
			} 
		}
	}

})();
